(647, '_site_transient_update_plugins', 'O:8:"stdClass":4:{s:12:"last_checked";i:1452566032;s:8:"response";a:5:{s:30:"advanced-custom-fields/acf.php";O:8:"stdClass":6:{s:2:"id";s:5:"21367";s:4:"slug";s:22:"advanced-custom-fields";s:6:"plugin";s:30:"advanced-custom-fields/acf.php";s:11:"new_version";s:5:"4.4.5";s:3:"url";s:53:"https://wordpress.org/plugins/advanced-custom-fields/";s:7:"package";s:71:"https://downloads.wordpress.org/plugin/advanced-custom-fields.4.4.5.zip";}s:19:"akismet/akismet.php";O:8:"stdClass":6:{s:2:"id";s:2:"15";s:4:"slug";s:7:"akismet";s:6:"plugin";s:19:"akismet/akismet.php";s:11:"new_version";s:5:"3.1.7";s:3:"url";s:38:"https://wordpress.org/plugins/akismet/";s:7:"package";s:56:"https://downloads.wordpress.org/plugin/akismet.3.1.7.zip";}s:45:"taxonomy-terms-order/taxonomy-terms-order.php";O:8:"stdClass":6:{s:2:"id";s:5:"23884";s:4:"slug";s:20:"taxonomy-terms-order";s:6:"plugin";s:45:"taxonomy-terms-order/taxonomy-terms-order.php";s:11:"new_version";s:5:"1.4.7";s:3:"url";s:51:"https://wordpress.org/plugins/taxonomy-terms-order/";s:7:"package";s:69:"https://downloads.wordpress.org/plugin/taxonomy-terms-order.1.4.7.zip";}s:19:"members/members.php";O:8:"stdClass":6:{s:2:"id";s:5:"10325";s:4:"slug";s:7:"members";s:6:"plugin";s:19:"members/members.php";s:11:"new_version";s:5:"1.1.1";s:3:"url";s:38:"https://wordpress.org/plugins/members/";s:7:"package";s:56:"https://downloads.wordpress.org/plugin/members.1.1.1.zip";}s:41:"rich-text-excerpts/rich-text-excerpts.php";O:8:"stdClass":6:{s:2:"id";s:5:"31196";s:4:"slug";s:18:"rich-text-excerpts";s:6:"plugin";s:41:"rich-text-excerpts/rich-text-excerpts.php";s:11:"new_version";s:5:"1.3.3";s:3:"url";s:49:"https://wordpress.org/plugins/rich-text-excerpts/";s:7:"package";s:67:"https://downloads.wordpress.org/plugin/rich-text-excerpts.1.3.3.zip";}}s:12:"translations";a:0:{}s:9:"no_update";a:8:{s:47:"admin-post-navigation/admin-post-navigation.php";O:8:"stdClass":7:{s:2:"id";s:4:"8284";s:4:"slug";s:21:"admin-post